subroutine iotk_read_INTEGER4(val,string,ierr)
  use iotk_base, dont_use => iotk_read_INTEGER4
  INTEGER(kind=__IOTK_INTEGER4), intent(out) :: val(:)
  character(len=*), intent(in) :: string
  integer, intent(out) :: ierr
  character(20) :: fmt
  integer :: pos,pos1,i
  fmt = "(i100)"
  pos = 0
  pos1 = 0
  ierr = 0
  do i = 1 , size(val)
    pos1 = scan(string(pos+1:),",")
    pos1 = pos1+pos
    if(pos1==pos) pos1=len(string)+1
    if(pos1==pos+1) ierr=__LINE__
    if(ierr/=0) return
    read(string(pos+1:pos1-1),fmt,iostat=iostat) val(i)
    if(iostat/=0) ierr=__LINE__
    if(ierr/=0) return
    pos=pos1
  end do
end subroutine iotk_read_INTEGER4


