subroutine iotk_read_COMPLEX2(val,string,ierr)
  use iotk_base, dont_use => iotk_read_COMPLEX2
  COMPLEX(kind=__IOTK_COMPLEX2), intent(out) :: val(:)
  character(len=*), intent(in) :: string
  integer, intent(out) :: ierr
  character(20) :: fmt
  integer :: pos,pos1,i
  real(kind=__IOTK_REAL2) :: tmp1,tmp2
  fmt = "(G100.100)"
  pos = 0
  pos1 = 0
  ierr = 0
  do i = 1 , size(val)
    if(pos>=len(string)) ierr=__LINE__
    if(ierr/=0) return
    pos1 = scan(string(pos+1:),",")
    pos1 = pos1+pos
    if(pos1==pos) ierr=__LINE__
    if(ierr/=0) return
    if(pos1==pos+1) ierr=__LINE__
    if(ierr/=0) return
    read(string(pos+1:pos1-1),fmt,iostat=iostat) tmp1
    if(iostat/=0) ierr=__LINE__
    if(ierr/=0) return
    pos=pos1
    pos1 = scan(string(pos+1:),",")
    pos1 = pos1+pos
    if(pos1==pos) pos1=len(string)+1
    if(pos1==pos+1) ierr=__LINE__
    if(ierr/=0) return
    read(string(pos+1:pos1-1),fmt,iostat=iostat) tmp2
    if(iostat/=0) ierr=__LINE__
    if(ierr/=0) return
    pos=pos1
    val(i) = cmplx(tmp1,tmp2)
  end do
end subroutine iotk_read_COMPLEX2


