subroutine iotk_write_attr_CHARACTER1_0(attr,name,val,first,ierr)
  use iotk_base, dont_use => iotk_write_attr_CHARACTER1_0
  implicit none
  character(*), intent(inout) :: attr
  character(*), intent(in)    :: name
  CHARACTER(kind=__IOTK_CHARACTER1,len=*), intent(in)  :: val 
  logical, optional, intent(in)  :: first
  integer, optional, intent(out) :: ierr
  integer :: ierrl
  character(len=6*len(val)) :: tmpchar
  character :: delim
  logical :: lquot,lapos
  ierrl = 0
  if(present(first)) then
    if(first) attr=" "
  end if
  if(.not.check_name(name)) ierrl = __LINE__
  if(ierrl/=0) goto 1
  lquot=scan(val,'"')>0
  lapos=scan(val,"'")>0
  tmpchar = val
  if(.not.lquot) then
    delim='"'
    tmpchar = iotk_deescape(tmpchar)
  else if(.not.lapos) then
    delim="'"
    tmpchar = iotk_deescape(tmpchar)
  else
    delim='"'
    tmpchar = iotk_deescape(tmpchar,quot=.true.,apos=.true.)
  end if
  call append(attr,name//'='//delim//trim(tmpchar)//delim,.true.,ierrl)
  if(ierrl/=0) goto 1
  if(.not.check_attr(attr)) ierrl = __LINE__
1 continue
  if(present(ierr)) then
    ierr = ierrl
  else
    if(ierrl/=0) call iotk_error(0,ierrl)
  end if
