#ifdef __IOTK_CHARACTER1
#if 9 <= __IOTK_MAXRANK
subroutine iotk_write_dat_CHARACTER1_9(unit,name,dat,fmt,ierr)
  use iotk_base, dont_use => iotk_write_dat_CHARACTER1_9
  implicit none
  integer,                intent(in)  :: unit
  character(*),           intent(in)  :: name
  CHARACTER (kind=__IOTK_CHARACTER1,len=*), intent(in)  :: dat (:,:,:,:,:,:,:,:,:) 
  character(*), optional, intent(in)  :: fmt
  integer,      optional, intent(out) :: ierr
  integer :: ierrl,lunit
  logical :: binary,raw
  integer(iotk_header_kind), parameter :: idummy=0
  character(300) :: usefmt,usefmt1
  character(iotk_attlenx) :: attr
!  write(0,*) "LEN IS",len(dat),"SIZE IS",iotk_size(dat)
  ierrl = 0
  lunit = iotk_physical_unit(unit)
  call iotk_unit_get(lunit,raw=raw,ierr=ierrl)
  ierrl=0
  usefmt = "!"
  if(present(fmt) .and. .not. raw) usefmt = fmt
! Per ora controllo solo che i caratteri di cui fare l'escape
! non compaiano nelle stringhe di formato e nel dato se e' un carattere.
  if(scan(usefmt,"<>&")/=0) ierrl = __LINE__
  if(ierrl/=0) goto 1
  if(any(scan(dat,"<>&")/=0)) ierrl = __LINE__
  if(ierrl/=0) goto 1
  call iotk_write_attr(attr,"type",lowercase("CHARACTER"),first=.true.,ierr=ierrl)
  if(ierrl/=0) goto 1
  call iotk_write_attr(attr,"size",iotk_size(dat),ierr=ierrl)
  if(ierrl/=0) goto 1
  call iotk_write_attr(attr,"kind",kind(dat),ierr=ierrl)
  if(ierrl/=0) goto 1
  call iotk_write_attr(attr,"len",len(dat),ierr=ierrl)
  if(ierrl/=0) goto 1
  if(usefmt/="!") call iotk_write_attr(attr,"fmt",trim(usefmt))
  if(ierrl/=0) goto 1
  call iotk_write_begin(unit,name,attr,ierrl)
  if(ierrl/=0) goto 1
! NB con iotk_write_begin l'unita' fisica puo' cambiare!
  lunit = iotk_physical_unit(unit)
  call iotk_unit_get(lunit,raw=raw,ierr=ierrl)
  ierrl=0
  call iotk_inquire(lunit,binary=binary,ierr=ierrl)
  if(ierrl/=0) goto 1
  if(binary) then
    if(raw) then
      write(lunit,iostat=iostat) dat
    else
      write(lunit,iostat=iostat) idummy,dat
    end if
  else
    usefmt = iotk_wfmt("CHARACTER",kind(dat),1,-1,usefmt)
    if(usefmt=="*") then
      write(lunit,*,iostat=iostat) dat
    else
      write(lunit,fmt=usefmt,iostat=iostat) dat
    end if
  end if
  if(iostat/=0) ierrl = __LINE__
  if(ierrl/=0) goto 1
  call iotk_write_end(unit,name,ierr=ierrl)
1 continue
  if(present(ierr)) then
    ierr = ierrl
  else
    if(ierrl/=0) call iotk_error(lunit,ierrl)
  end if
end subroutine iotk_write_dat_CHARACTER1_9

recursive subroutine iotk_scan_dat_aux_CHARACTER1_9(unit,dat,rkind,rlen,fmt,ierr)
  use iotk_base, dont_use => iotk_scan_dat_aux_CHARACTER1_9
  implicit none
  integer,         intent(in)  :: unit
  CHARACTER (kind=__IOTK_CHARACTER1,len=*), intent(out) :: dat (:,:,:,:,:,:,:,:,:)
  integer,         intent(in)  :: rkind
  integer,         intent(in)  :: rlen
  character(*),    intent(in)  :: fmt
  integer,         intent(out) :: ierr
  integer(iotk_header_kind) :: idummy
  character(len(fmt)) :: fmt1
  logical :: raw,binary
  integer :: lunit
  CHARACTER (kind=kind(dat), len=rlen) :: dattmp(lbound(dat,1):ubound(dat,1),lbound(dat,2):ubound(dat,2),lbound(dat,3):ubound(dat,3), &
lbound(dat,4):ubound(dat,4),lbound(dat,5):ubound(dat,5),lbound(dat,6):ubound(dat,6), &
lbound(dat,7):ubound(dat,7),lbound(dat,8):ubound(dat,8),lbound(dat,9):ubound(dat,9))
  lunit = iotk_physical_unit(unit)
  ierr = 0
  call iotk_unit_get(lunit,raw=raw,ierr=ierr)
  ierr = 0
  call iotk_inquire(unit=lunit,binary=binary,ierr=ierr)
  if(ierr/=0) return
  idummy = 0
  if(.not.binary) fmt1 = iotk_rfmt("CHARACTER",fmt)
  if(binary) then
    if(raw) then
      read(lunit,iostat=iostat) dattmp
    else
      read(lunit,iostat=iostat) idummy,dattmp
    end if
  else
    if(fmt1=="*") then
      read(lunit,fmt=*,  iostat=iostat) dattmp
    else
      read(lunit,fmt=fmt1,iostat=iostat) dattmp
    end if
  end if
  if(iotk_len(dattmp) <= iotk_len(dat)) then
    dat (:,:,:,:,:,:,:,:,:) = dattmp (:,:,:,:,:,:,:,:,:)
  else
    dat (:,:,:,:,:,:,:,:,:) = dattmp (:,:,:,:,:,:,:,:,:) (1:iotk_len(dat))
  end if
  if(idummy/=0) ierr = __LINE__
  if(iostat/=0) ierr = __LINE__
  if(ierr/=0) return
end subroutine iotk_scan_dat_aux_CHARACTER1_9

subroutine iotk_scan_dat_CHARACTER1_9(unit,name,dat,found,default,ierr)
  use iotk_base, dont_use => iotk_scan_dat_CHARACTER1_9
  implicit none
  integer,                   intent(in)  :: unit
  character(*),              intent(in)  :: name
  CHARACTER (kind=__IOTK_CHARACTER1,len=*),           intent(out) :: dat (:,:,:,:,:,:,:,:,:)
  logical,         optional, intent(out) :: found
  CHARACTER (kind=__IOTK_CHARACTER1,len=*), optional, intent(in)  :: default (:,:,:,:,:,:,:,:,:)
  integer,         optional, intent(out) :: ierr
  integer :: ierrl,ierrl2
  integer :: rkind,rsize,rlen
  character(9) :: rtype
  character(300) :: fmt
  character(iotk_attlenx) :: attr
  ierrl = 0
  ierrl2 = 0
  call iotk_scan_begin(unit,name,attr,ierr=ierrl)
  if(ierrl/=0) goto 1
  call iotk_parse_dat(attr,rtype,rkind,rsize,rlen,fmt,ierrl)
  if(ierrl/=0) then
    call iotk_scan_end(unit,name,ierr=ierrl2)
    goto 1
  end if
  if(ierrl/=0) goto 1
  if(rtype=="NA") rtype = "CHARACTER"
  if(rkind==-1) rkind = kind(dat)
  if(rsize==-1) rsize = iotk_size(dat)
  if(rlen ==-1) rlen  = iotk_len(dat)
  if(rtype /= "CHARACTER") ierrl = __LINE__
  if(ierrl/=0) goto 1
  if(rsize /= iotk_size(dat)) ierrl = __LINE__
  if(ierrl/=0) goto 1
  call iotk_scan_dat_aux(unit,dat,rkind,rlen,fmt,ierrl)
  call iotk_scan_end(unit,name,ierr=ierrl2)
  if(ierrl2/=0) ierrl=ierrl2
1 continue
  if(present(found)) then
    found = .false.
    if(ierrl==0) found = .true.
  end if
  if(present(default)) then
    if(ierrl/=0) dat = default
  end if
  if(present(ierr)) then
    ierr = ierrl
  else
    if(ierrl>0 .or. (.not.present(found) .and. .not.present(default))) call iotk_error(0,ierrl)
  end if
end subroutine iotk_scan_dat_CHARACTER1_9




